const path = require('path');
const TerserPlugin = require('terser-webpack-plugin');

module.exports = {
  entry: {
    background: './src/background.js',
    content: './src/content.js',
  },
  output: {
    filename: '[name].bundle.js',
    path: path.resolve(__dirname, 'dist'),
  },
  mode: process.env.NODE_ENV === 'production' ? 'production' : 'development',
  devtool: process.env.NODE_ENV === 'production' ? false : 'inline-source-map',
  module: {
    rules: [
      {
        test: /\.css$/,
        use: ['style-loader', 'css-loader'],
      },
    ],
  },
  optimization: {
    minimizer: [new TerserPlugin()],
  },
};

{
  "manifest_version": 3,
  "name": "Quick Fields",
  "version": "1.0",
  "description": "A Chrome extension for Quick Fieldsing on web pages",
  "permissions": [
    "activeTab",
    "storage"
  ],
  "background": {
    "service_worker": "dist/background.bundle.js"
  },
  "content_scripts": [
    {
      "matches": ["<all_urls>"],
      "js": ["dist/content.bundle.js"]
    }
  ],
  "commands": {
    "_execute_action": {
      "suggested_key": {
        "default": "Ctr+Alt+L"
      },
      "description": "Activate the command palette"
    }
  },
  "action": {
    "default_title": "Quick Fields"
  }
}

try {
  console.log('Background script loaded');
  // Background script logic will be implemented here
} catch (error) {
  console.error('Error in background script:', error);
}

try {
  console.log('Content script loaded');
  import './styles.css';
  // Content script logic will be implemented here
} catch (error) {
  console.error('Error in content script:', error);
}

module.exports = {
  env: {
    browser: true,
    es2021: true,
    webextensions: true,
  },
  extends: 'eslint:recommended',
  parserOptions: {
    ecmaVersion: 12,
    sourceType: 'module',
  },
  rules: {
    // Add any specific rules here
  },
};
